let logoutForm=document.getElementById("logout"),logoutButton=document.getElementById("submitLogout"),searchBox=document.getElementById("search");function search(){fetch(`/api/search/?query=${searchBox.value}`).then(e=>e.json()).then(e=>{let t=document.getElementById("foundItems");""==searchBox.value?t.innerHTML="":e.forEach(e=>{let o=document.getElementById(e._id);if(o)o=o;else{let o=document.createElement("a");o.classList.add("p-3"),o.classList.add("mt-1"),o.classList.add("shadow-sm"),o.style.border="1px solid gainsboro",o.style.borderRadius="10px",o.href=`/user/${e._id}`,o.id=e._id,o.innerHTML=`\n              <img height="32" src="${e.pictureUrl}">\n              <h6>${e.fullName}</h6>\n            `,t.appendChild(o)}})}).catch(e=>console.error(e))}logoutButton.addEventListener("click",()=>logoutForm.submit()),searchBox.addEventListener("keyup",search);