let body=document.body,themeButton=document.getElementById("theme"),themeIcon=document.getElementById("themeIcon"),background=document.querySelectorAll(".bg-white"),text=document.querySelectorAll(".text-dark"),buttons=document.querySelectorAll(".btn-dark");function changeTheme(){background.forEach(t=>{t.classList.contains("bg-white")?(t.classList.remove("bg-white"),t.classList.add("bg-dark")):(t.classList.remove("bg-dark"),t.classList.add("bg-white"))}),text.forEach(t=>{t.classList.contains("text-white")?(t.classList.remove("text-white"),t.classList.add("text-dark")):(t.classList.remove("text-dark"),t.classList.add("text-white"))}),buttons.forEach(t=>{t.classList.contains("btn-dark")?(t.classList.remove("btn-dark"),t.classList.add("btn-light")):(t.classList.remove("btn-light"),t.classList.add("btn-dark"))}),themeIcon.classList.contains("fa-sun")?(themeIcon.classList.remove("fa-sun"),themeIcon.classList.add("fa-moon")):(themeIcon.classList.remove("fa-moon"),themeIcon.classList.add("fa-sun"))}themeButton.addEventListener("click",changeTheme);